import { createSlice } from "@reduxjs/toolkit";
import { toggleActions } from "./index";
const initialCartState = { items: [], totalQuantity: 0 };
const cartSlice = createSlice({
  name: "cart",
  initialState: initialCartState,
  reducers: {
    add(state, action) {
      const { price, title } = action.payload;
      const existingItem = state.items.find((item) => item.title === title);
      if (!existingItem) {
        state.items.push({ title, price, quantity: 1, total: price });
      } else {
        existingItem.quantity++;
        existingItem.total += price;
      }
      state.totalQuantity++;
    },

    decrease(state, action) {
      const title = action.payload;
      const existingItem = state.items.find((item) => item.title === title);
      if (existingItem.quantity === 1) {
        state.items = state.items.filter((item) => item.title != title);
      } else {
        existingItem.quantity--;
        existingItem.total -= existingItem.price;
        state.totalQuantity--;
      }
    },
    increase(state, action) {
      const title = action.payload;
      const existingItem = state.items.find((item) => item.title === title);
      existingItem.total += existingItem.price;
      existingItem.quantity++;
      state.totalQuantity++;
    },
  },
});

const sendCartData = (cart) => {
  return async (dispatch) => {
    //we can do any asnyc or side effect tasks in here.
    dispatch(
      toggleActions.showNotification({
        status: "pending",
        title: "Sending...",
        message: "Sending cart data!",
      })
    );

    const sendRequest = async () => {
      const res = await fetch(
        "https://pook-9bc90-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (!res.ok) {
        throw new Error("Sending cart data failed.");
      }
    };

    try {
      await sendRequest();
      dispatch(
        toggleActions.showNotification({
          status: "success",
          title: "Success!",
          message: "Sent cart data successfully!",
        })
      );
    } catch (error) {
      dispatch(
        toggleActions.showNotification({
          status: "error",
          title: "Error!",
          message: "Sending cart data failed!",
        })
      );
    }
  };
};

export default cartSlice;
