import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { toggleActions } from "./store/index";
import Notification from "./components/UI/Notification";

let isInitial = true;
//밖에 선언했으므로, 바뀌지 않는다.

function App() {
  const dispatch = useDispatch();
  const toggleShow = useSelector((state) => state.toggle.toggleShow);
  const cart = useSelector((state) => state.cart);
  const notification = useSelector((state) => state.toggle.notification);

  useEffect(() => {
    const sendCartData = async () => {
      const res = await fetch(
        "https://pook-9bc90-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (!res.ok) {
        throw new Error("Sending cart data failed.");
      }
      const resData = await res.json();

      dispatch(
        toggleActions.showNotification({
          status: "success",
          title: "Success!",
          message: "Sent cart data successfully!",
        })
      );
    };
    if (isInitial) {
      isInitial = false;
      return;
    }

    sendCartData().catch((error) => {
      dispatch(
        toggleActions.showNotification({
          status: "error",
          title: "Error!",
          message: "Sending cart data failed!",
        })
      );
    });
  }, [cart, dispatch]);

  return (
    <>
      {notification && (
        <Notification
          status={notification.status}
          title={notification.title}
          message={notification.message}
        />
      )}
      <Layout>
        {toggleShow && <Cart />}
        <Products />
      </Layout>
    </>
  );
}

export default App;
