import { configureStore, createSlice } from "@reduxjs/toolkit";

const initialToggleState = { toggleShow: false };
const toggleSlice = createSlice({
  name: "toggle",
  initialState: initialToggleState,
  reducers: {
    toggle(state) {
      state.toggleShow = !state.toggleShow;
    },
  },
});

const initialCartState = [{ title: "test", price: 0, quantity: 0, total: 0 }];

const cartSlice = createSlice({
  name: "cart",
  initialState: initialCartState,
  reducers: {
    add(state, action) {
      const { price, title } = action.payload;
      state.forEach((el) => {
        if (el.title === title) {
          el.quantity += 1;
          el.total += price;
          return;
        }
      });
      state.push({ title, price, quantity: 1, total: price });
    },
    decrease(state) {},
  },
});

const store = configureStore({
  reducer: { toggle: toggleSlice.reducer, cart: cartSlice.reducer },
});

const toggleActions = toggleSlice.actions;
const cartActions = cartSlice.actions;

export { store, toggleActions, cartActions };
