import { createSlice } from "@reduxjs/toolkit";

const initialCartState = { items: [], totalQuantity: 0 };
const cartSlice = createSlice({
  name: "cart",
  initialState: initialCartState,
  reducers: {
    add(state, action) {
      const { price, title } = action.payload;
      const existingItem = state.items.find((item) => item.title === title);
      if (!existingItem) {
        state.items.push({ title, price, quantity: 1, total: price });
      } else {
        existingItem.quantity++;
        existingItem.total += price;
      }
      state.totalQuantity++;
    },

    decrease(state, action) {
      const title = action.payload;
      for (let i = 0; i < state.length; i++) {
        if (state[i].title === title) {
          if (state[i].quantity === 1) {
            state.splice(i, 1);
          } else {
            state[i].quantity--;
            state[i].total -= state[i].price;
          }
        }
      }
    },
    increase(state, action) {
      const title = action.payload;
      // console.log(title);
      for (let i = 0; i < state.length; i++) {
        if (state[i].title === title) {
          state[i].quantity++;
          state[i].total += state[i].price;
        }
      }
    },
  },
});

export default cartSlice;
