import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { toggleActions } from "./store/index";

function App() {
  const dispatch = useDispatch();
  const toggleShow = useSelector((state) => state.toggle.toggleShow);
  const cart = useSelector((state) => state.cart);
  const notification = useSelector((state) => state.toggle.notification);
  useEffect(() => {
    const sendCartData = async () => {
      dispatch(
        toggleActions.showNotification({
          status: "pending",
          title: "Sending...",
          message: "Sending cart data!",
        })
      );
      const res = await fetch(
        "https://pook-9bc90-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (!res.ok) {
        throw new Error("Sending cart data failed.");
      }
      const resData = await res.json();

      dispatch(
        toggleActions.showNotification({
          status: "success",
          title: "Success...",
          message: "Sent cart data successfully!",
        })
      );
    };
    sendCartData().catch((error) => {
      dispatch(
        toggleActions.showNotification({
          status: "error",
          title: "Error!",
          message: "Sending cart data failed!",
        })
      );
    });
  }, [cart, dispatch]);

  return (
    <Layout>
      {toggleShow && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
